/* ONE dark UI variables
** ---------------------------------------------- */
/* Official variables
** These variables must be defined in every theme
** Source: https://github.com/atom/atom/blob/master/static/variables/ui-variables.less
** ---------------------------------------------- */
/* Custom variables
** These variables are only used in this theme
** ---------------------------------------------- */
/* Packages variables
** These variables are used to override packages
** ---------------------------------------------- */
/* Debug
** Output variables to the top of the UI
** ------------------------------------- */
.about {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  -webkit-user-select: none;
  cursor: default;
  overflow: auto;
  text-align: center;
  font-size: 1.25em;
  line-height: 1.4;
  padding: 4em;
  color: #acaea3;
  background-color: #363731;
}
.about button {
  cursor: default;
}
.about a:focus {
  color: inherit;
}
.about img,
.about a {
  -webkit-user-drag: none;
}
.about .input-checkbox {
  margin-top: -0.2em;
}
.about .group-start {
  margin-top: 4em;
}
.about .group-item {
  margin-top: 1.5em;
}
.about-container {
  width: 100%;
  max-width: 500px;
}
.about-atom-io:hover .about-logo {
  color: #40a977;
}
.about-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 1em auto;
  color: #e6eccb;
  transition: color 0.2s;
}
.about-version-container:hover {
  color: #d0d1cc;
}
.about-version-container:active {
  color: #f5f5f4;
}
.about-version {
  margin-right: .5em;
  font-size: 1.25em;
  vertical-align: middle;
}
.about-header-release-notes {
  vertical-align: middle;
  margin-left: 1em;
}
.about-updates-box {
  display: flex;
  align-items: center;
  padding: 10px;
  border: 1px solid #262722;
  border-radius: 6px;
  background-color: #41423b;
}
.about-updates-status {
  flex: 1;
  margin-left: .5em;
  text-align: left;
}
.about-updates-item,
.about-default-update-message .about-updates-label {
  display: block;
}
.about-updates-label {
  color: rgba(172, 174, 163, 0.6);
}
.about-updates-label.is-strong {
  color: #acaea3;
}
.about-updates-version {
  margin: 0 .4em;
}
.about-updates-release-notes {
  margin: 0 1em 0 1.5em;
}
.about-auto-updates {
  margin-top: 1em;
}
.about-auto-updates input {
  margin-right: .5em;
}
.about-love .icon::before {
  position: relative;
  width: auto;
  height: auto;
  margin-right: 0;
  font-size: 1.5em;
  vertical-align: text-top;
}
.about-love .icon-logo-github::before {
  font-size: 3.6em;
  height: .36em;
}
.about-credits {
  color: rgba(172, 174, 163, 0.6);
}
.about-release-notes {
  color: #0088ff;
}
.about-release-notes:hover {
  color: #4dacff;
}
