css:
/*
* Modal
*/
.quick-query-modal-message {
  padding-right: 10px;
}
.quick-query-modal-message span.icon-x {
  position: absolute;
  top: 4px;
  right: 0;
  color: rgba(255, 255, 255, 0.07);
}
.quick-query-modal-message span.icon-clippy {
  margin-left: 5px;
  cursor: pointer;
}
atom-panel.modal:hover .quick-query-modal-message span.icon-clippy {
  animation: in 0.8s linear 0s;
}
@keyframes in {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
atom-panel.modal:not(:hover) .quick-query-modal-message span.icon-clippy {
  opacity: 0;
}
atom-panel.modal:not(:hover) .quick-query-modal-message.animated span.icon-clippy {
  animation: out 0.8s linear 0s;
}
@keyframes out {
  from {
    opacity: 1;
  }
  to {
    opacity: 0;
  }
}
/*
* Editor
*/
#quick-query-default-is-null {
  border-width: 1px;
  border-style: solid;
  border-color: rgba(0, 0, 0, 0.5);
  height: 25px;
  padding: 5px;
  border-radius: 2px;
  font-style: italic;
}
/*
	Bootstrap grid system was removed in atom v1.10
*/
.dialog.quick-query-connect .col-sm-3,
.quick-query-editor .col-sm-3,
.dialog.quick-query-connect .col-sm-9,
.quick-query-editor .col-sm-9,
.dialog.quick-query-connect .col-sm-6,
.quick-query-editor .col-sm-6,
.dialog.quick-query-connect .col-sm-12,
.quick-query-editor .col-sm-12 {
  padding-right: 15px;
  padding-left: 15px;
}
.dialog.quick-query-connect .row,
.quick-query-editor .row {
  clear: both;
}
@media (min-width: 544px) {
  .dialog.quick-query-connect .row .col-sm-1,
  .quick-query-editor .row .col-sm-1 {
    float: left;
    width: 8%;
  }
  .dialog.quick-query-connect .row .col-sm-2,
  .quick-query-editor .row .col-sm-2 {
    float: left;
    width: 16%;
  }
  .dialog.quick-query-connect .row .col-sm-3,
  .quick-query-editor .row .col-sm-3 {
    float: left;
    width: 25%;
  }
  .dialog.quick-query-connect .row .col-sm-9,
  .quick-query-editor .row .col-sm-9 {
    float: left;
    width: 75%;
  }
  .dialog.quick-query-connect .row .col-sm-6,
  .quick-query-editor .row .col-sm-6 {
    float: left;
    width: 50%;
  }
}
